function cartMenu(){$(".target").swap(),$("nav.lower").clone().appendTo(".upper .menu-wrapper").addClass("mobile")}function addtoCart(){$("#product--add .btn").on("click",function(t){t.preventDefault(),$("#header .cart-toggle").toggleClass("full")})}function bannerSlider(){$(".banner.slider").carousel({controls:!1,infinite:!0})}function productHover(){$(".collection-grid--item").hover(function(){$(".collection-grid--item_children",this).stop(!0,!0).transition({opacity:1})},function(){$(".collection-grid--item_children",this).stop(!0,!0).transition({opacity:0})})}$(document).ready(function(){cartMenu(),addtoCart(),productHover(),bannerSlider(),$("input[type=number]").number(),$(".tab").tabs({mobileMaxWidth:"0"})});